import React from 'react';
import './App.css';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {lang: true}
    this.Klik = this.Klik.bind(this);
  }

  Klik(e){
    e.preventDefault();
    this.setState({lang: !this.state.lang});
  }

  render(){
    return (
      <div className="App">
        <header className="App-header">
          <p>
            Hello World
          </p>
          <buttto onClick={this.Klik}>Change Language to Indonesia</buttto>
        </header>
      </div>
    );
  }
}

export default App;
