import React from 'react';
import './App.css';
import { connect } from 'react-redux';

class Body extends React.Component{
    todoRef = React.createRef();
    jmlRef = React.createRef();

    _handleNewTodo = (e) =>{
        let newTodo = this.todoRef.current.value;
        let newJml = this.jmlRef.current.value;
        this.props.addNewTodo(newTodo);
        this.props.addNewJml(newJml);
        
        e.preventDefault();
        this.todoRef.current.value = '';
    };

    render(){
        const { todos } = this.props;
        return (
            <div className="App App-header">
                <div>Daftar Kegiatan</div>
                <form onSubmit={this._handleNewTodo}>
                    <label>Kegiatan <input ref={this.todoRef} /></label>
                    <label>Jumlah Kegiatan <input ref={this.jmlRef} /></label>
                </form>
                <ul>
                    {todos.map((item, index) => <li key={index}>{item}</li>)}
                </ul>
            </div>
        );
    }
    }

    const mapStateToProps = state => ({
        todos: state.todos
    })

    const mapDispatchToProps = dispatch => ({
        addNewTodo: todo => dispatch({type: 'ADD TODO', todo}),
        addNewJml: jml => dispatch({type: 'ADD TODO', jml})
})

export default connect(mapStateToProps, mapDispatchToProps)(Body);
