import React from 'react';
import './App.css';

const gaya = React.createContext({
  latar: 'english',
  onChange: (e) => {
    e.preventDefault();
    this.setState(() => ({ latar: !this.state.latar }));
  }
});

class App extends React.Component{
  // // constructor(props){
  // //   super(props);
  //   state = {
