import React from 'react';
import './App.css';

const gaya = React.createContext('english');

class App extends React.Component{
  constructor(){
    super();
    this.Klik = () => {
      this.setState(() => ({latar: !this.state.latar}));
    };
    this.state = {
      latar: 'english',
      onChange: this.Klik
    };
  }


  render(){
      return (
        <div className="App">
          <header className="App-header">
            <Halaman />
          </header>
        </div>
      );
  }
}

export default App;

class Halaman extends React.Component{
  render(){
    return(
      <div>
        <Tema />
      </div>
    );
  }
}

class Tema extends React.Component {
  render() {
    const {} = this.context;
    return (
      <div style={{
        backgroundColor: gaya === 'english' ? 'dark': 'white',
        width: '100vw',
        height: '100vh',
        fontSize: 24,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        color: gaya === 'english' ?'white' :'dark'
      }}>
        {gaya === 'english' ? <label>Hello World</label>: <label>Halo Dunia</label>}
        <button style={{marginTop: 30}} onClick={this.onChange}></button>
      </div>
    );
  }
}
Tema.contextType = gaya;
