import React from 'react';
import './App.css';

const gaya = React.createContext('english');

class App extends React.Component{
  constructor(){
    super();
    this.Klik = () => {
      this.setState(() => ({latar: !this.state.latar}));
    };
    this.state = {
      latar: 'english',
      onChange: this.Klik
    };
  }


  render(){
    if(this.state.lang === true){
      return (
        <div className="App">
          <header className="App-header">
            <p>
              Hello World
            </p>
            <button onClick={this.Klik}>Change Language to Indonesia</button>
          </header>
        </div>
      );
    }else{
      return (
        <div className="App">
          <header className="App-header2">
            <p>
              Halo Dunia
            </p>
            <button onClick={this.Klik}>Ubah Bahasa ke Inggris</button>
          </header>
        </div>
      );
    }
  }
}

export default App;

class Halaman extends React.Component{
  render(){
    return(
      <div></div>
    );
  }
}

class Tema extends React.Component {
  render() {
    return (
      <div style={{
        backgroundColor: gaya === 'english' ? 'dark': 'white',
        width: '100vw',
        height: '100vh',
        fontSize: 24,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        color: gaya === 'english' ?'white' :'dark'
      }}>
        Hello World
        <button style={{marginTop: 30}} onClick={this.Klik}></button>
      </div>
    );
  }
}
