import React from 'react';
import './App.css';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {lang: true};
    this.Klik = this.Klik.bind(this);
  }

  Klik(e){
    e.preventDefault();
    this.setState({lang: !this.state.lang});
  }

  render(){
    if(this.state.lang === true){
      return (
        <div>
          <header>
            <p>
              Hello World
            </p>
            <buttto onClick={this.Klik}>Change Language to Indonesia</buttto>
          </header>
        </div>
      );
    }else{
      return (
        <div>
          <header>
            <p>
              Halo Dunia
            </p>
            <buttto onClick={this.Klik}>Ubah Bahasa ke Inggris</buttto>
          </header>
        </div>
      );
    }
  }
}

export default App;
