import React from 'react';
import './App.css';

const gaya = React.createContext('english');

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      latar: 'english',
      onChange: this.Klik
    };
    // this.state = {
      //   latar: 'english',
      //   onChange: this.Klik
      // };
    // this.Klik = this.Klik.bind(this);
    this.Klik = (e) =>{
      e.preventDefault();
      this.setState(() => ({latar: !this.state.latar}));
    };
    }



  render(){
    const { latar, onChange } = this.state;
    return (
      <div className="App">
        <header className="App-header">
          <Halaman
            latar={latar}
            onChange={onChange}
          />
        </header>
      </div>
    );
  }
}

export default App;

class Halaman extends React.Component{
  render(){
    const { latar, onChange } = this.props;
    return(
      <div>
        <Tema
          latar={latar}
          onChange={onChange}
        />
      </div>
    );
  }
}

class Tema extends React.Component {
  render() {
    const { onChange } = this.props;
    const gaya = this.context;
    // console.log(gaya)
    return (
      <div style={{
        backgroundColor: gaya === 'english' ? 'dark': 'white',
        width: '100vw',
        height: '100vh',
        fontSize: 24,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        color: gaya === 'english' ?'white' :'dark'
      }}>
        {gaya === 'english' ? <label>Hello World</label>: <label>Halo Dunia</label>}
        <button style={{marginTop: 30}} onClick={onChange}>Change Language to Indonesia</button>
      </div>
    );
  }
}
Tema.contextType = gaya;
