import React from 'react';
import './App.css';
import { ReactComponent } from '*.svg';

const gaya = React.createContext('dark');

class App extends React.Component{
  constructor(props){
    super(props);
    this.Klik = this.Klik.bind(this);
  }

  Klik(e){
    e.preventDefault();
    this.setState({lang: !this.state.lang});
  }

  render(){
    if(this.state.lang === true){
      return (
        <div className="App">
          <header className="App-header">
            <p>
              Hello World
            </p>
            <button onClick={this.Klik}>Change Language to Indonesia</button>
          </header>
        </div>
      );
    }else{
      return (
        <div className="App">
          <header className="App-header2">
            <p>
              Halo Dunia
            </p>
            <button onClick={this.Klik}>Ubah Bahasa ke Inggris</button>
          </header>
        </div>
      );
    }
  }
}

export default App;

class Halaman extends React.Component{
  render(){
    return(
      <div></div>
    );
  }
}

class Tema extends React.Component {
  render() {
    return (
      <div></div>
    );
  }
}
